mapper.select:
SELECT a.id, a.uuid, u.name, d.dept_name AS deptName, a.amount, a.reason, `at`.type, a.`status`, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i') AS createTime, IF(a.user_id=#{currentUserId}, "true", "false") AS mine FROM tb_amect a JOIN tb_amect_type `at` ON a.type_id = `at`.id JOIN tb_user u ON u.id = a.user_id JOIN tb_dept d ON u.dept_id = d.id WHERE 1=1
AND u.id = #{userId}
AND u.name LIKE '%${name}%'
AND u.dept_id = #{deptId}
AND at.id = #{typeId}
AND a.create_time BETWEEN #{startDate} AND #{endDate}
AND a.`status` = #{status}
ORDER BY a.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_amect a
JOIN tb_amect_type `at` ON a.type_id = `at`.id
JOIN tb_user u ON u.id = a.user_id
JOIN tb_dept d ON u.dept_id = d.id
WHERE 1=1

    AND u.id = #{userId}


    AND u.name LIKE '%${name}%'


    AND u.dept_id = #{deptId}


    AND at.id = #{typeId}


    AND a.create_time BETWEEN #{startDate} AND #{endDate}


    AND a.`status` = #{status}

